'use strict';

var AnnotationPage = require('../models/pages/AnnotationPage.js');

const annotationEditPage = async ({ page, selectors, grafanaVersion, request }, use, testInfo) => {
  const annotationPage = new AnnotationPage.AnnotationPage({ page, selectors, grafanaVersion, request, testInfo });
  await annotationPage.goto();
  const annotationEditPage2 = await annotationPage.clickAddNew();
  await use(annotationEditPage2);
};

exports.annotationEditPage = annotationEditPage;
