'use strict';

var AnnotationEditPage = require('../../models/pages/AnnotationEditPage.js');

const gotoAnnotationEditPage = async ({ request, page, selectors, grafanaVersion }, use, testInfo) => {
  await use(async (args) => {
    const annotationEditPage = new AnnotationEditPage.AnnotationEditPage({ page, selectors, grafanaVersion, request, testInfo }, args);
    await annotationEditPage.goto();
    return annotationEditPage;
  });
};

exports.gotoAnnotationEditPage = gotoAnnotationEditPage;
