'use strict';

var DashboardPage = require('../../models/pages/DashboardPage.js');

const gotoDashboardPage = async ({ request, page, selectors, grafanaVersion }, use, testInfo) => {
  await use(async (args) => {
    const dashboardPage = new DashboardPage.DashboardPage({ page, selectors, grafanaVersion, request, testInfo }, args);
    await dashboardPage.goto();
    return dashboardPage;
  });
};

exports.gotoDashboardPage = gotoDashboardPage;
