'use strict';

var VariablePage = require('../../models/pages/VariablePage.js');

const gotoVariablePage = async ({ request, page, selectors, grafanaVersion }, use, testInfo) => {
  await use(async (args) => {
    const variablePage = new VariablePage.VariablePage({ page, selectors, grafanaVersion, request, testInfo }, args);
    await variablePage.goto();
    return variablePage;
  });
};

exports.gotoVariablePage = gotoVariablePage;
