'use strict';

var path = require('path');
require('@playwright/test');
var GrafanaAPIClient = require('../models/GrafanaAPIClient.js');

const adminClientStorageState = path.join(process.cwd(), `playwright/.auth/grafanaAPICredentials.json`);
const grafanaAPIClient = async ({ browser, grafanaAPICredentials }, use) => {
  const context = await browser.newContext({ storageState: void 0 });
  const loginReq = await context.request.post("/login", { data: grafanaAPICredentials });
  if (!loginReq.ok()) {
    console.log(
      `Could not login to grafana using credentials '${grafanaAPICredentials?.user}'. Find information on how user can be managed in the plugin-e2e docs: https://grafana.com/developers/plugin-tools/e2e-test-a-plugin/use-authentication#managing-users  : ${await loginReq.text()}`
    );
  }
  await context.request.storageState({ path: adminClientStorageState });
  await use(new GrafanaAPIClient.GrafanaAPIClient(context.request));
};

exports.grafanaAPIClient = grafanaAPIClient;
