'use strict';

var e2eSelectors = require('@grafana/e2e-selectors');
var versionedConstants = require('../selectors/versionedConstants.js');
var versionedAPIs = require('../selectors/versionedAPIs.js');

const selectors = async ({ grafanaVersion }, use) => {
  await use({
    components: e2eSelectors.resolveSelectors(e2eSelectors.versionedComponents, grafanaVersion),
    pages: e2eSelectors.resolveSelectors(e2eSelectors.versionedPages, grafanaVersion),
    constants: e2eSelectors.resolveSelectors(versionedConstants.versionedConstants, grafanaVersion),
    apis: e2eSelectors.resolveSelectors(versionedAPIs.versionedAPIs, grafanaVersion)
  });
};

exports.selectors = selectors;
