'use strict';

var test = require('@playwright/test');
var utils = require('./utils.js');

async function toHaveChecked(radioGroup, expected, options) {
  try {
    await test.expect(radioGroup.locator().getByLabel(expected)).toBeChecked(options);
    return {
      pass: true,
      expected,
      message: () => `Value successfully selected`
    };
  } catch (err) {
    return {
      message: () => utils.getMessage(expected.toString(), err instanceof Error ? err.toString() : "Unknown error"),
      pass: false
    };
  }
}

exports.toHaveChecked = toHaveChecked;
