'use strict';

class ComponentBase {
  constructor(ctx, element) {
    this.ctx = ctx;
    this.element = element;
  }
  locator(selectorOrLocator, options) {
    if (!selectorOrLocator) {
      return this.element;
    }
    return this.element.locator(selectorOrLocator, options);
  }
}

exports.ComponentBase = ComponentBase;
